// Analyze performance and decide on action
const analytics = $node['Fetch Latest Analytics'].json.result?.data?.json || {};
const currentPrompt = $node['Fetch Current Prompt'].json.result?.data?.json?.value || "Default Prompt";

// Thresholds
const MIN_CONVERSION_RATE = 20; // 20%
const conversionRate = parseFloat(analytics.conversionRate || 0);

if (conversionRate < MIN_CONVERSION_RATE) {
  return {
    json: {
      action: "rewrite_prompt",
      reason: `Low conversion rate (${conversionRate}%)`,
      currentPrompt: currentPrompt,
      analytics: analytics
    }
  };
} else {
  return {
    json: {
      action: "none",
      reason: "Performance is good",
      analytics: analytics
    }
  };
}

